local weaponNames = { "opt", "Crowbar", "Knife", "Deagle", "Pistols", "Silenced Pistol", "Glock", "H.U.G.E-249", "Rifles", "Assault rifles", "SMGs", "Shotguns", "UMP Prototype", "Teleporter", "Goomba Stomping", "Gravity", "Props", "Explosives", "Fire", "Suffocation" }net.Receive("myStats", function( str )	local data = net.ReadTable()		local frame1 = vgui.Create("DFrame")	frame1:SetPos(100,100)	frame1:SetSize(300, 190)	frame1:SetTitle(data[2].."'s Stats")	frame1:Center()	frame1:MakePopup()		mystatsView = vgui.Create("DListView")	mystatsView:SetParent(frame1)	mystatsView:SetPos(5, 30)	mystatsView:SetSize(frame1:GetWide() - 10, frame1:GetTall() - 35)	mystatsView:SetMultiSelect(false)	mystatsView:AddColumn("Weapon")	mystatsView:AddColumn("Kills")	mystatsView:Clear()		/* mystatsView.OnRowRightClick = function(panel, line)		local hazzah = DermaMenu()		hazzah:SetParent(frame1)		hazzah:SetPos( frame1:CursorPos() )		hazzah:AddOption("Set Preferred", function()			RunConsoleCommand("pref_weapon", getIDFromWeapName(mystatsView:GetLine(line):GetValue(1)))		end)	end */ 		local data = string.Explode(":", data[1])	local total = 0	for k,v in pairs(data) do		if k != 1 then			mystatsView:AddLine(weaponNames[k], tonumber(v))			total = total + tonumber(v)		end	end	mystatsView:AddLine("Total", total)	mystatsView:SortByColumn(2, true)end)function getIDFromWeapName(name)	for k,v in pairs(weaponNames) do		if v == name then			return k		end	endend